#include <iostream>
using namespace std;
int main() {
    int choice; float temp;
    cout << "1.F→C  2.C→F: "; cin >> choice;
    cout << "Temp: "; cin >> temp;
    if(choice==1) cout << (temp-32)*5/9 << "°C";
    else cout << temp*9/5+32 << "°F";
}
```
