void solveQuadratic(float a, float b, float c) {
    float discriminant = b*b - 4*a*c;

    if (discriminant > 0) {
        float x1 = (-b + sqrt(discriminant)) / (2*a);
        float x2 = (-b - sqrt(discriminant)) / (2*a);
        cout << "x = " << x1 << ", " << x2;
    } else if (discriminant == 0) {
        float x = -b / (2*a);
        cout << "x = " << x;
    } else {
        cout << "No real roots";
    }
}

int main() {
