void pattern(int n) {
    // Upper half
    for(int i = 1; i <= n; i++) {
        // Leading spaces
        for(int j = 0; j < n-i; j++)
            cout << " ";
        // Stars
        for(int j = 0; j < i; j++)
            cout << "* ";
        cout << endl;
    }
    // Lower half
    for(int i = n-1; i >= 1; i--) {
        for(int j = 0; j < n-i; j++)
            cout << " ";
        for(int j = 0; j < i; j++)
            cout << "* ";
        cout << endl;
    }
}
