class Circle {
    double radius;
    double centerX, centerY;

public:
    // 1. Default: radius=1, center=(0,0)
    Circle() : radius(1), centerX(0), centerY(0) {}

    // 2. Given radius, center=(0,0)
    Circle(double r) : radius(r), centerX(0), centerY(0) {}

    // 3. Given center, radius=1
    Circle(double x, double y) : radius(1), centerX(x), centerY(y) {}

    // 4. Given radius and center
    Circle(double r, double x, double y) 
        : radius(r), centerX(x), centerY(y) {}

    // 5. Copy constructor
    Circle(const Circle& c) 
        : radius(c.radius), centerX(c.centerX), centerY(c.centerY) {}
};

int main() {
    Circle c1;              // Default
    Circle c2(5);           // Radius only
    Circle c3(2, 3);        // Center only
    Circle c4(5, 2, 3);     // All params
    Circle c5(c4);          // Copy
    return 0;
}
```

