void pattern(int n) {
    for(int i = 1; i <= n; i++) {
        for(int s = 0; s < n-i; s++)
            cout << " ";
        for(int j = 0; j < i; j++)
            cout << "* ";
        cout << endl;
    }
    for(int i = n-1; i >= 1; i--) {
        for(int s = 0; s < n-i; s++)
            cout << " ";
        for(int j = 0; j < i; j++)
            cout << "* ";
        cout << endl;
    }
}
