int power(int k, int n) {
    if (n == 0) return 1;
    if (n == 1) return k;
    return k * power(k, n-1);
}

// Optimized O(log n):
int power(int k, int n) {
    if (n == 0) return 1;
    int half = power(k, n/2);
    if (n % 2 == 0)
        return half * half;
    return k * half * half;
}
